/**
 * @private
 */
export default class TokenTypes<T> {
    tokenTypes: {
        type: T;
        regex: RegExp;
        enabled: boolean;
        skip: boolean;
    }[];
    constructor();
    disable(type: T): TokenTypes<T>;
    enable(type: T, enabled?: boolean): TokenTypes<T>;
    isEnabled(type: T): boolean;
    peek(source: string, position: number): {
        item: {
            type: T;
            regex: RegExp;
            enabled: boolean;
            skip: boolean;
        };
        result: RegExpExecArray;
    };
    token(type: T, pattern: RegExp | string, skip?: boolean): TokenTypes<T>;
}
